mapper.select:
select *
from team_member
where member_id = #{id}
and password = #{pwd}

	select member_id
	from team_member		
	where member_name=#{memberName}		
	and birth = #{birth}		
	and phone = #{phone}

select password
from team_member
where member_id=#{memberId}
and member_name=#{memberName}
and birth = #{birth}
and phone = #{phone}

mapper.update:
update team_member
set
member_id=#{memberId},
    
    
password=#{password}
    
where member_name=#{memberName}

update team_member
set
mileage=#{mileage}
where member_name=#{memberName}
