mapper.select:
select *
from team_member
where member_id = #{id}
and password = #{pwd}

	select member_id
	from team_member		
	where member_name=#{memberName}		
	and birth = #{birth}		
	and phone = #{phone}

select password
from team_member
where member_id=#{memberId}
and member_name=#{memberName}
and birth = #{birth}
and phone = #{phone}

mapper.update:
update team_member
set
member_id=#{memberId},
    
    
password=#{password}
    
where member_name=#{memberName}

update team_member
set
mileage=mileage+#{mileage}
where member_name=#{memberName}

update team_member
set
grade=#{grade}
where member_id = #{name}

mapper.select:
SELECT * FROM ( SELECT * FROM team_bills ORDER BY pay_date DESC )
WHERE ROWNUM <= 2
and seller=#{logId}

SELECT * FROM ( SELECT * FROM team_bills ORDER BY pay_date DESC )
WHERE ROWNUM <= 2
and buyer=#{logId}

mapper.update:
update team_member
set
phone=#{phone}
where member_id=#{memberId}
