mapper.select:
select a.*
from (select rownum rn, r.*
	  from ( select *
	  		 from team_item

			     categories = #{categories} 
			     and trade = #{trade} 
			     and game = #{game}

				     and servers = #{servers}



				     and ${searchType} like '%'||#{searchData}||'%'



			     order by ${sort} ${order}

		   ) r
	 ) a
where a.rn > (#{page} - 1) * 10

	and   a.rn <= #{page} * 10

select count(item_number)
from team_item

	categories = #{categories}
	and trade = #{trade}
    and game = #{game}

		and servers = #{servers}


		and ${searchType} like '%'||#{searchData}||'%'

mapper.update:
update team_item 
set count=count-#{count} 
where item_number=#{itemNumber}

update team_member

	set mileage = mileage - #{total}


	set mileage = mileage + #{total}

where member_id = #{buyer}

update team_member

	set mileage = mileage + #{total}


	set mileage = mileage - #{total}

where member_id = #{seller}

update team_bills
set pay_step = 'completed', pay_date = sysdate
where bills_number = #{billsNumber}

update team_item
set count = count + #{count}
where item_number = #{itemNumber}

mapper.select:
select mileage
from team_member
where member_id = #{logId}
