mapper.select:
select a.*
from (select rownum rn, r.*
	  from ( select *
	  		 from team_item

			     categories = #{categories} 
			     and trade = #{trade} 


				     and ${searchType} like '%'||#{searchData}||'%'



			     order by ${sort} ${order}

		   ) r
	 ) a
where a.rn > (#{page} - 1) * 10

	and   a.rn <= #{page} * 10

select count(item_number)
from team_item


		categories = #{categories}
		 and 


		${searchType} like '%'||#{searchData}||'%'



	order by ${sort} ${order}
