mapper.select:
select a.*
from (select rownum rn, r.*
	  from ( select *
	  		 from team_item


				     categories = #{categories}
				      and 


				     ${searchType} like '%'||#{searchData}||'%'



			     order by ${sort} ${order}

		   ) r
	 ) a
where a.rn > (#{page} - 1) * 5

	and   a.rn <= #{page} * 5
