mapper.update:
update	team_item
set		item_name = #{itemName}

			,servers = #{servers}

		,item_info = #{itemInfo}
		,price = #{price}
		,count = #{count}
		,image = #{image}
		,up_date = sysdate
where	item_number = #{itemNumber}

update 	team_bills
set		pay_step = #{payStep}
where	item_number = #{itemNum}

update team_bills
set pay_step = 'deal'
where bills_number = #{billsNumber}
